mapper.update:
update rent
rentForm=#{rentForm}, rentPrice=#{rentPrice}, rentCurrency=#{rentCurrency}, rentPhone=#{rentPhone}, rentType=#{rentType}, rentRooms=#{rentRooms}, rentGuests=#{rentGuests}, rentSingleBeds=#{rentSingleBeds}, rentDoubleBeds=#{rentDoubleBeds}, rentBaths=#{rentBaths}, rentArchitecture=#{rentArchitecture}, rentSurface=#{rentSurface}, rentParty=#{rentParty}, rentAge=#{rentAge}, rentDescription=#{rentDescription}, rentPetsAllowed=#{rentPetsAllowed}, rentParkingPlace=#{rentParkingPlace}, rentSmokersAllowed=#{rentSmokersAllowed}, rentAddDate=#{rentAddDate}
where rentId=#{rentId}

mapper.select:
    	select count(*) from rent inner join address on rent.addressId=address.addressId
    	inner join currency on rent.rentCurrency=currency.currencyName

    		address.addressLatitude between #{lowRent.address.addressLatitude} and #{highRent.address.addressLatitude}
			and address.addressLongitude between #{lowRent.address.addressLongitude} and #{highRent.address.addressLongitude}
			and rentPrice*currency.currencyConversionRate >= #{lowRent.rentPrice}
    		and #{highRent.rentPrice} >= rentPrice*currency.currencyConversionRate
    		and rentType=#{lowRent.rentType}
    		and rentSurface >= #{lowRent.rentSurface}
    		and #{highRent.rentSurface} >= rentSurface
    		and rentRooms >= #{lowRent.rentRooms}
    		and #{highRent.rentRooms} >= rentRooms
    		and rentGuests >= #{lowRent.rentGuests}
    		and #{highRent.rentGuests} >= rentGuests
    		and rentSingleBeds >= #{lowRent.rentSingleBeds}
    		and #{highRent.rentSingleBeds} >= rentSingleBeds
    		and rentDoubleBeds >= #{lowRent.rentDoubleBeds}
    		and #{highRent.rentDoubleBeds} >= rentDoubleBeds
    		and rentBaths >=#{lowRent.rentBaths}
    		and #{highRent.rentBaths} >= rentBaths
    		 and rentArchitecture=#{lowRent.rentArchitecture}
    		and (rentPetsAllowed=#{lowRent.rentPetsAllowed} or rentPetsAllowed=#{highRent.rentPetsAllowed})
    		and (rentParkingPlace=#{lowRent.rentParkingPlace} or rentParkingPlace=#{highRent.rentParkingPlace})
    		and (rentSmokersAllowed=#{lowRent.rentSmokersAllowed} or rentSmokersAllowed=#{highRent.rentSmokersAllowed})
    		and rentStatus=#{lowRent.rentStatus} and rentForm=#{lowRent.rentForm}

    	select rent.rentId as rentId, rentPrice, rentCurrency, rentSurface, rentRooms, 
    	rentBaths, rentType, rentArchitecture, rentAddDate, address.addressId as addressId,
    	addressStreetName, addressNeighbourhood, addressLatitude,
    	addressLongitude, min(rentImageId) as rentImageId, 
    	concat(#{appUrl}, rentImageURI) as rentImageURI
    	from rent inner join address on rent.addressId=address.addressId
    	left join rent_image on rent_image.rentId = rent.rentId
    	inner join currency on rent.rentCurrency=currency.currencyName

    		address.addressLatitude between #{lowRent.address.addressLatitude} and #{highRent.address.addressLatitude}
			and address.addressLongitude between #{lowRent.address.addressLongitude} and #{highRent.address.addressLongitude}
			and rentPrice*currency.currencyConversionRate >= #{lowRent.rentPrice}
    		and #{highRent.rentPrice} >= rentPrice*currency.currencyConversionRate
    		and rentType=#{lowRent.rentType}
    		and rentSurface >= #{lowRent.rentSurface}
    		and #{highRent.rentSurface} >= rentSurface
    		and rentRooms >= #{lowRent.rentRooms}
    		and #{highRent.rentRooms} >= rentRooms
    		and rentGuests >= #{lowRent.rentGuests}
    		and #{highRent.rentGuests} >= rentGuests
    		and rentSingleBeds >= #{lowRent.rentSingleBeds}
    		and #{highRent.rentSingleBeds} >= rentSingleBeds
    		and rentDoubleBeds >= #{lowRent.rentDoubleBeds}
    		and #{highRent.rentDoubleBeds} >= rentDoubleBeds
    		and rentBaths >=#{lowRent.rentBaths}
    		and #{highRent.rentBaths} >= rentBaths
    		 and rentArchitecture=#{lowRent.rentArchitecture}
    		and (rentPetsAllowed=#{lowRent.rentPetsAllowed} or rentPetsAllowed=#{highRent.rentPetsAllowed})
    		and (rentParkingPlace=#{lowRent.rentParkingPlace} or rentParkingPlace=#{highRent.rentParkingPlace})
    		and (rentSmokersAllowed=#{lowRent.rentSmokersAllowed} or rentSmokersAllowed=#{highRent.rentSmokersAllowed})
    		and rentStatus=#{lowRent.rentStatus} and rentForm=#{lowRent.rentForm}

    	group by rent.rentId
    	order by rent.rentAddDate desc, rent.rentId desc limit #{pageSize}

    	select rent.rentId as rentId, rentPrice, rentCurrency, rentSurface, rentRooms, 
    	rentBaths, rentType, rentArchitecture, rentAddDate, address.addressId as addressId,
    	addressStreetName, addressNeighbourhood, addressLatitude,
    	addressLongitude, min(rentImageId) as rentImageId, 
    	concat(#{appUrl}, rentImageURI) as rentImageURI
    	from rent inner join address on rent.addressId=address.addressId
    	left join rent_image on rent_image.rentId = rent.rentId
    	inner join currency on rent.rentCurrency=currency.currencyName

    		address.addressLatitude between #{lowRent.address.addressLatitude} and #{highRent.address.addressLatitude}
			and address.addressLongitude between #{lowRent.address.addressLongitude} and #{highRent.address.addressLongitude}
			and rentPrice*currency.currencyConversionRate >= #{lowRent.rentPrice}
    		and #{highRent.rentPrice} >= rentPrice*currency.currencyConversionRate
    		and rentType=#{lowRent.rentType}
    		and rentSurface >= #{lowRent.rentSurface}
    		and #{highRent.rentSurface} >= rentSurface
    		and rentRooms >= #{lowRent.rentRooms}
    		and #{highRent.rentRooms} >= rentRooms
    		and rentGuests >= #{lowRent.rentGuests}
    		and #{highRent.rentGuests} >= rentGuests
    		and rentSingleBeds >= #{lowRent.rentSingleBeds}
    		and #{highRent.rentSingleBeds} >= rentSingleBeds
    		and rentDoubleBeds >= #{lowRent.rentDoubleBeds}
    		and #{highRent.rentDoubleBeds} >= rentDoubleBeds
    		and rentBaths >=#{lowRent.rentBaths}
    		and #{highRent.rentBaths} >= rentBaths
    		 and rentArchitecture=#{lowRent.rentArchitecture}
    		and (rentPetsAllowed=#{lowRent.rentPetsAllowed} or rentPetsAllowed=#{highRent.rentPetsAllowed})
    		and (rentParkingPlace=#{lowRent.rentParkingPlace} or rentParkingPlace=#{highRent.rentParkingPlace})
    		and (rentSmokersAllowed=#{lowRent.rentSmokersAllowed} or rentSmokersAllowed=#{highRent.rentSmokersAllowed})
    		and rentStatus=#{lowRent.rentStatus} and rentForm=#{lowRent.rentForm}
    		and #{highRent.rentAddDate} >= rent.rentAddDate
    		and (#{highRent.rentId} > rent.rentId or #{highRent.rentAddDate} > rent.rentAddDate)

    	group by rent.rentId
    	order by rent.rentAddDate desc, rent.rentId desc limit #{pageSize}
